/*
Special Pythagorean triplet
Problem 9
A Pythagorean triplet is a set of three natural numbers, a < b < c, for which,

a2 + b2 = c2
For example, 32 + 42 = 9 + 16 = 25 = 52.

There exists exactly one Pythagorean triplet for which a + b + c = 1000.
Find the product abc.
*/

// c^2 = (1000 - a - b) * (1000-a-b) = a^2 + b^2
// 1000 = k
// k^2 - ka-kb-ka+a^2+ab-kb+ab+b^2 = a^2 + b^2
// a^2 + b^2 = k^2-2ka-2kb+2ab + a^2 + b^2
// 0 = k^2-2ka-2kb + 2ab
// 0 = 500k-ka-kb+ab
// ka-ab = 500k-kb
// a * (k-b) = 500k-kb
// a = (500k-kb) / (k-b)

const getA = (b) => (500000-1000*b)/(1000-b)

const getC = (b) => Math.sqrt(getA(b)*getA(b) + b*b)

const isNatural = (num) => typeof(num)==="number" && num === Math.floor(num) && num > 0 && num != Infinity

const specialPythagoreanTriplet = () => {
  let a, c
  for (let b = 999; b > 1; b--) {
    a = getA(b)
    c = getC(b)
    if (isNatural(a) && isNatural(b)) {
      return (a*b*c)
    }
  }
}

console.log(specialPythagoreanTriplet())

/*
Summation of primes
Problem 10

The sum of the primes below 10 is 2 + 3 + 5 + 7 = 17.

Find the sum of all the primes below two million.

*/

const primeSum = (max) => {
  let primes = [], sieve = [], candidate = 2, multiple;
  while (candidate < max) {
    if (sieve[candidate] !== "compound") {
      primes.push(candidate);
      for  (multiple = candidate*candidate; multiple < max; multiple += candidate) {
          sieve[multiple] = "compound"
      }
    }
    candidate += candidate===2 ? 1 : 2
  }
  return primes.reduce((accumulator, currentValue) => accumulator + currentValue, 0)
}
console.log(primeSum(2000000))


