/*
Highly divisible triangular number
Problem 12

The sequence of triangle numbers is generated by adding the natural numbers. So the 7th triangle number would be 1 + 2 + 3 + 4 + 5 + 6 + 7 = 28. The first ten terms would be:

1, 3, 6, 10, 15, 21, 28, 36, 45, 55, ...

Let us list the factors of the first seven triangle numbers:

     1: 1
     3: 1,3
     6: 1,2,3,6
    10: 1,2,5,10
    15: 1,3,5,15
    21: 1,3,7,21
    28: 1,2,4,7,14,28

We can see that 28 is the first triangle number to have over five divisors.

What is the value of the first triangle number to have over five hundred divisors?

*/


/*
Naive solution:
//////////////////
let divisorCounter = (num) => {
  if (num===1) 1
  let factor1 = 2, factor2 = num, divCount = 2
  while (factor1 < factor2) {
    if (factor2 % factor1 === 0) {
      factor2 /= factor1
      divCount += factor1 === factor2 ? 1 : 2
    } else {
      factor1 += factor1 = 2 ? 1 : 2
    }
  }
  return divCount
}

let highlyDivisibleTriangularNumber = (divisors) => {
  let triNum = 1, divCount = 1
  for (let i = 2; divCount < 500; i++) {
    triNum += i;
    divCount = divisorCounter(triNum)
  }
  return triNum
}
*/

let pfObjectMaker = (num) => {
  let primeFactorsObject = {}, factor = 2;
  while (num > 1) {
    if (num % factor === 0) {
      primeFactorsObject[factor.toString()] = primeFactorsObject[factor.toString()] ? primeFactorsObject[factor.toString()] + 1 : 1;
      num /= factor
    } else {
      factor += factor === 2 ? 1 : 2
    }
  }
  return primeFactorsObject
}

let primeLister = (length) => {
  let primes = [], sieve = [], candidate = 2, maxMultiple = Math.pow(2, 26), multiple;
  while (primes.length < length && candidate < maxMultiple) {
    if (sieve[candidate] !== "compound") {
      primes.push(candidate);
      for  (multiple = candidate*candidate; multiple <= maxMultiple; multiple += candidate) {
          sieve[multiple] = "compound"
      }
    }
    candidate += candidate===2 ? 1 : 2
  }
  return primes
}

// the number of divisors of a number d(n) =
// the product of (each of the exponents of its prime factorization + 1)

// 500 = 2^2 * 5^3 = 2 * 2 * 5 * 5 * 5
// so the prime factorization of the lowest number with 500 or more divisors has exponents 1, 1, 4, 4, 4.
// The highly composite number with 500+ divisors then is 2^4 * 3^4 * 5^4 * 7 * 11

let divisorCounter = (num) => Object.values(pfObjectMaker(num)).reduce((accumulator, currentValue) => accumulator*(currentValue + 1), 1)

let antiprimeFinder = (divisorCount) => {
  let divCtPF = pfObjectMaker(divisorCount)
  let exponents = []
  Object.keys(divCtPF).forEach(base => {
    let addition = new Array(divCtPF[base]).fill(base-1)
    exponents = [...exponents, ...addition]
  });
  exponents.sort((a, b) => b - a)
  let primes = primeLister(exponents.length)
  return primes.reduce((accumulator, currentValue, currentIndex) =>
    accumulator * Math.pow(currentValue, exponents[currentIndex]), 1)
}

let isTriangular = (num) => {
  let n = Math.floor(Math.sqrt(2*num))
  return num === n*(n+1)/2
}

let firstTriangleAfterAntiprime = (antiprime) => {
  let triNum=0
  for (let i = antiprime; triNum<1; i++) {
    if (isTriangular(i)) {triNum = i}
  }
  return triNum
}

// the nth triangle = n*(n+1)/2

let nFromTriangle = (triNum) => Math.floor(Math.sqrt(2*triNum))

let nextTriangle = (triNum) => triNum + nFromTriangle(triNum) + 1

let lowestHighlyDivisibleTriangularNumber = (divisorCount) => {
  let triNum = firstTriangleAfterAntiprime(antiprimeFinder(500))
  while (divisorCounter(triNum) < 500) {
    triNum = nextTriangle(triNum)
  }
  return triNum
}

console.log(lowestHighlyDivisibleTriangularNumber(500))


